import React, { Fragment, useState } from 'react';
import { Profesional } from './profesional';
import { Accordion, Alert } from 'react-bootstrap';
import { Buscador } from './buscador';

export const Especialidades = ({ especialidades }) => {

    const [especialidadFiltrada, setEspecialidadFiltrada] = useState([]);

    const buscarEspecialidad = (especialidad) => {
        setEspecialidadFiltrada(especialidades.filter(e => e.titulo === especialidad));
    }

    return (
        <Fragment>
            {!especialidadFiltrada && <div className="container pt-2">
                <Alert variant="dark">
                    <Buscador especialidades={especialidades} buscarEspecialidad={buscarEspecialidad} />
                </Alert>
                <Accordion>
                    {especialidades.map((especialidad, index) => {
                        return (<Accordion.Item eventKey={index} key={especialidad.id}><div className="row">
                            <Accordion.Header>
                                <h2>{especialidad.titulo}</h2>
                            </Accordion.Header>
                            <Accordion.Body>
                                <div className="row">
                                    {especialidad.profesionales.map((profesional, index) => {
                                        return (
                                            <div className="col-12 col-md-3" key={profesional.id}><Profesional profesional={profesional} /></div>
                                        )
                                    })}
                                </div>
                            </Accordion.Body>
                        </div></Accordion.Item>)
                    })
                    }
                </Accordion>
            </div>}
        </Fragment>
    );
};
