import React, { useState, useEffect } from 'react';
import { BASEURL } from '../lib/baseurl';
import ReactCardFlip from 'react-card-flip';


export const Profesional = ({ profesional, profesionalSeleccionado }) => {

    const [imagen, setImagen] = useState('');
    const [flipped, setFlipped] = useState(false);
    useEffect(() => {

        if (profesional.imagen) {
            setImagen(profesional.imagen);
        } else {
            if (profesional.genero === 'Masculino') {
                setImagen(BASEURL + '/wp-content/plugins/profesionales/doc_m.png');
            } else {
                setImagen(BASEURL + '/wp-content/plugins/profesionales/doc_f.png');
            }
        }
    }, [])

    useEffect(() => {
        console.log(profesionalSeleccionado)
    }, [profesionalSeleccionado])

    const changeFlip = () => {
        setFlipped(!flipped);
    }

    return (
        <div className={"card" + ((profesionalSeleccionado && profesional.apellido === profesionalSeleccionado) ? ' selected' : '')}>
            <ReactCardFlip isFlipped={flipped} flipDirection="horizontal">
            <div onMouseOver={changeFlip} class="flip-box-front" style={{
                backgroundColor: "rgb(247, 247, 247)", borderColor: "rgba(0, 0, 0, 0)", borderRadius: "6px", borderStyle: "solid", borderWidth: "0px", color: "rgb(51, 51, 51)", transitionDuration: "0.5s" 
            }} >
                <div className="flip-box-front-inner">
                    <div className="flip-box-grafix flip-box-image">
                        <img src={imagen} alt="" className="img-fluid" width="175" height="175" />
                    </div>
                    <h2 class="flip-box-heading" style={{ color: "#333333" }}> {profesional.apellido}, {profesional.nombre}</h2>
                    {profesional.matricula}
                </div>
            </div>
            <div className="flip-box-back" onMouseLeave={changeFlip}>back</div>
            </ReactCardFlip>
        </div >
    );
};
