import React, { useState } from 'react';
import Autocomplete from 'react-autocomplete';

export const Buscador = () => {
    const [value, setValue] = useState('');
    return (
        <div>
            <Autocomplete
            className="form-control"
            menuStyle={{zIndex: '9999', position: 'absolute', top: '100px', left: '0', right: '0', border: '1px solid #ccc', background: '#fff', overflow: 'auto', maxHeight: '300px', padding: '0'}}
                getItemValue={(item) => item.label}
                items={[
                    { label: 'apple' },
                    { label: 'banana' },
                    { label: 'pear' }
                ]}
                shouldItemRender={(item, value) => item.label.toLowerCase().indexOf(value.toLowerCase()) > -1}
                renderItem={(item, isHighlighted) =>
                    <div className="dropresultados" style={{ background: isHighlighted ? 'lightgray' : 'white' }}>
                        {item.label}
                    </div>
                }
                value={value}
                onChange={(e) => setValue(e.target.value)}
                onSelect={(val) => setValue(val)}
            />
        </div>
    );
};
